import React, { useState } from "react";
import axios from "axios";
import BookForm from "./BookForm";

const BookList = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookData, setBookData] = useState({
    title: "",
    author: "",
    publisher: "",
    description: "",
    image: "",
  });
  const [bookList, setBookList] = useState([]);

  const handleSearch = async () => {
    if (!searchTitle) return alert("Ï±Ö Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");
    setBookList([]);
    try {
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: { query: searchTitle },
      });
      let bookInfo = response.data;

      if (bookInfo.length > 0) {
        setBookList(bookInfo); // Ï±Ö Î¶¨Ïä§Ìä∏ Ï†ÄÏû•
        console.log(bookList);
      } else {
        alert("Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.");
      }
    } catch (error) {
      console.error("Ï±Ö Í≤ÄÏÉâ Ïã§Ìå®:", error);
      alert("Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  const handleBookSelect = (book) => {
    setBookData({
      title: book.title,
      author: book.author || "",
      publisher: book.publisher || "",
      description: book.description || "",
      image: book.image || "",
    });
  };

  // Ï±Ö Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏ Ìï®Ïàò
  const handleUpdateBook = async () => {
    try {
      // ÏÑúÎ≤ÑÏóê Ï±Ö ÏóÖÎç∞Ïù¥Ìä∏ ÏöîÏ≤≠
      const response = await axios.post(`${backServer}/api/books`, bookData);
      if (response.status === 200) {
        alert("Ï±ÖÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§.");
      }
    } catch (error) {
      console.error("Ï±Ö ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:", error);
      alert("Ï±Ö ÏóÖÎç∞Ïù¥Ìä∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };
  return (
    <div>
      <div className="page-title">üìö Book Insert</div>
      {/* Ï±Ö Í≤ÄÏÉâ ÏûÖÎ†• */}
      <div className="search-box">
        <input
          type="text"
          placeholder="Ï±Ö Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞(Ï†úÎ™© ÏûÖÎ†•)..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>Í≤ÄÏÉâ</button>
      </div>

      <div>
        {bookList.length > 0 && (
          <ul className="book-api-list">
            {bookList.map((book, index) => (
              <li key={index} onClick={() => handleBookSelect(book)}>
                {book.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <BookForm bookData={bookData} setBookData={setBookData} />
      <div className="update-button-wrap">
        <button className="update-button" onClick={handleUpdateBook}>
          Ï±Ö ÏóÖÎç∞Ïù¥Ìä∏
        </button>
      </div>
    </div>
  );
};

export default BookList;
