import React, { useState } from "react";
import axios from "axios";

const BookList = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookData, setBookData] = useState({
    title: "",
    author: "",
    publisher: "",
    description: "",
    link: "",
  });

  const handleSearch = async () => {
    if (!searchTitle) return alert("책 제목을 입력하세요!");

    try {
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: { query: searchTitle },
      });
      let bookInfo = response.data;

      if (!Array.isArray(bookInfo)) {
        bookInfo = [bookInfo];
      }

      if (bookInfo.length > 0) {
        const book = bookInfo[0];
        setBookData({
          title: book.title.replace(/<b>|<\/b>/g, ""),
          author: book.author || "",
          publisher: book.publisher || "",
          description: book.description || "",
          image: book.link || "",
        });
        console.log(book.link);
      } else {
        alert("검색 결과가 없습니다.");
      }
    } catch (error) {
      console.error("책 검색 실패:", error);
      alert("검색 중 오류가 발생했습니다.");
    }
  };

  return (
    <section className="section" style={{ width: "100%" }}>
      <div className="page-title">📚 Book List</div>

      {/* 책 검색 입력 */}
      <div className="search-box">
        <input
          type="text"
          placeholder="책 제목을 입력하세요..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      {/* 책 정보 입력 폼 (직접 입력 가능) */}
      <div className="book-form">
        <label>제목:</label>
        <input
          type="text"
          value={bookData.title}
          onChange={(e) => setBookData({ ...bookData, title: e.target.value })}
          placeholder="제목을 입력하세요"
        />

        <label>저자:</label>
        <input
          type="text"
          value={bookData.author}
          onChange={(e) => setBookData({ ...bookData, author: e.target.value })}
          placeholder="저자를 입력하세요"
        />

        <label>출판사:</label>
        <input
          type="text"
          value={bookData.publisher}
          onChange={(e) =>
            setBookData({ ...bookData, publisher: e.target.value })
          }
          placeholder="출판사를 입력하세요"
        />

        <label>설명:</label>
        <textarea
          value={bookData.description}
          onChange={(e) =>
            setBookData({ ...bookData, description: e.target.value })
          }
          placeholder="책 설명을 입력하세요"
        />

        <label>표지 이미지 :</label>

        {/* 이미지 미리보기 */}
        {bookData.image && (
          <img src={bookData.link} alt="책 표지" width="100" />
        )}
      </div>
    </section>
  );
};

export default BookList;
