import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import BookUpdate from "./BookUpdate";
import "./book.css";
import axios from "axios";
import PageNavi from "./PageNavi";

const BookList = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [bookList, setBookList] = useState([]);
  const [reqPage, setReqPage] = useState(1);
  const [pi, setPi] = useState({});

  useEffect(() => {
    axios
      .get(`${backServer}/api/books/${reqPage}`)
      .then((res) => {
        console.log(res);
        setBookList(res.data.list); //목록
        setPi(res.data.pi); //페이징
      })
      .catch((err) => {
        console.log(err);
      });
  }, [reqPage]);

  return (
    <section className="section" style={{ width: "100%" }}>
      <div className="page-title">Book List</div>
      <Link to="/book/update" className="btn-primary">
        책등록
      </Link>
      <div className="book-list-wrap">
        <ul className="list-wrap">
          {bookList.map((book, i) => {
            return <BookItem key={i} book={book} />;
          })}
        </ul>
      </div>
      <div className="board-paging-warp">
        <PageNavi pi={pi} reqPage={reqPage} setReqPage={setReqPage} />
      </div>
    </section>
  );
};

const BookItem = (props) => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const book = props.book;
  const navigate = useNavigate();

  const [stockCount, setStockCount] = useState(book.stockCount); // 재고 상태 추가

  // 재고 수정 함수
  const handleStockChange = () => {
    if (stockCount < 0) {
      alert("재고는 0 이상이어야 합니다.");
      return;
    }

    // 서버로 재고 수정된 값 전송
    axios
      .put(`${backServer}/api/books/${book.id}/update`, { stockCount })
      .then((res) => {
        console.log("재고가 성공적으로 업데이트되었습니다.", res);
      })
      .catch((err) => {
        console.log("재고 업데이트 중 오류 발생", err);
      });
  };

  return (
    <li className="posting-item">
      <div className="posting-img">
        <img
          src={book.image ? book.image : "/image/default_img.png"}
          alt={book.title}
          onClick={() => {
            navigate(`/board/view/${book.id}`); // Navigate to a detailed view based on book ID
          }}
        />
      </div>
      <div className="posting-info">
        <div className="posting-title">{book.title}</div>
        <div className="posting-sub-info">
          <span>저자 : {book.author}</span>
          <span>출판사 : {book.publisher}</span>
          <span>판매 : {book.salesCount}</span>
          <span>현재 재고 : {stockCount}</span>
        </div>

        {/* 재고 수정 입력 필드 및 수정 버튼 */}
        <div className="stock-controls">
          <input
            type="number"
            value={stockCount}
            onChange={(e) => setStockCount(parseInt(e.target.value))}
            min="0"
          />
          <button onClick={handleStockChange}>재고수정</button>
        </div>
      </div>
    </li>
  );
};

export default BookList;
