import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

const NAVER_CLIENT_ID = process.env.NAVER_CLIENT_ID;
const NAVER_CLIENT_SECRET = process.env.NAVER_CLIENT_SECRET;

// ðŸ” ë„¤ì´ë²„ APIë¡œ ì±… ê²€ìƒ‰ (ë¹„ë™ê¸°)
export const searchBooks = createAsyncThunk(
  "books/searchBooks",
  async (query) => {
    const response = await axios.get(
      `https://openapi.naver.com/v1/search/book.json?query=${query}`,
      {
        headers: {
          "X-Naver-Client-Id": NAVER_CLIENT_ID,
          "X-Naver-Client-Secret": NAVER_CLIENT_SECRET,
        },
      }
    );
    return response.data.items;
  }
);

const bookSlice = createSlice({
  name: "books",
  initialState: {
    bookList: [], // ë„¤ì´ë²„ ê²€ìƒ‰ ê²°ê³¼
    selectedBook: null, // ì„ íƒí•œ ì±… ì •ë³´
    status: "idle", // API ìƒíƒœ ê´€ë¦¬
  },
  reducers: {
    setSelectedBook: (state, action) => {
      state.selectedBook = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(searchBooks.pending, (state) => {
        state.status = "loading";
      })
      .addCase(searchBooks.fulfilled, (state, action) => {
        state.bookList = action.payload;
        state.status = "succeeded";
      })
      .addCase(searchBooks.rejected, (state) => {
        state.status = "failed";
      });
  },
});

export const { setSelectedBook } = bookSlice.actions;
export default bookSlice.reducer;
