import React, { useState } from "react";
import axios from "axios";
import BookForm from "./BookFrom";

const BookList = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookData, setBookData] = useState({
    title: "",
    author: "",
    publisher: "",
    description: "",
    image: "",
  });
  const [bookList, setBookList] = useState([]);

  const handleSearch = async () => {
    if (!searchTitle) return alert("책 제목을 입력하세요!");

    try {
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: { query: searchTitle },
      });
      let bookInfo = response.data;

      if (!Array.isArray(bookInfo)) {
        bookInfo = [bookInfo];
      }

      if (bookInfo.length > 0) {
        setBookList(bookInfo); // 책 리스트 저장
      } else {
        alert("검색 결과가 없습니다.");
      }
    } catch (error) {
      console.error("책 검색 실패:", error);
      alert("검색 중 오류가 발생했습니다.");
    }
  };

  const handleBookSelect = (book) => {
    setBookData({
      title: book.title,
      author: book.author || "",
      publisher: book.publisher || "",
      description: book.description || "",
      image: book.image || "",
    });
  };

  // 책 정보 업데이트 함수
  const handleUpdateBook = async () => {
    try {
      // 서버에 책 업데이트 요청
      const response = await axios.put(`/api/books/${bookData.id}`, bookData);
      if (response.status === 200) {
        alert("책이 성공적으로 업데이트되었습니다.");
      }
    } catch (error) {
      console.error("책 업데이트 실패:", error);
      alert("책 업데이트 중 오류가 발생했습니다.");
    }
  };
  return (
    <div>
      {/* 책 검색 입력 */}
      <div className="search-box">
        <input
          type="text"
          placeholder="책 제목을 입력하세요..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>

      <div>
        {bookList.length > 0 && (
          <ul className="book-api-list">
            {bookList.map((book, index) => (
              <li key={index} onClick={() => handleBookSelect(book)}>
                {book.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      <BookForm bookData={bookData} setBookData={setBookData} />
      <div>
        <button onClick={handleUpdateBook}>책 업데이트</button>
      </div>
    </div>
  );
};

export default BookList;
