import React, { useState } from "react";
import axios from "axios";

const BookUpdate = () => {
  const NAVER_CLIENT_ID = process.env.REACT_APP_NAVER_CLIENT_ID;
  const NAVER_CLIENT_SECRET = process.env.REACT_APP_NAVER_CLIENT_SECRET;
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookList, setBookList] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [loading, setLoading] = useState(false);

  // 책 검색 처리
  const handleSearch = async () => {
    if (!searchTitle) return;

    setLoading(true); // 로딩 시작

    try {
      // 백엔드 서버를 통해 네이버 API에 요청
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: {
          query: searchTitle,
        },
      });

      // 네이버 API에서 XML 응답을 받으면 적절히 파싱하여 bookList에 저장
      const bookData = response.data;
      setBookList(bookData); // 실제 API 응답에 맞게 파싱 필요
    } catch (error) {
      console.error("책 검색 실패:", error);
    } finally {
      setLoading(false); // 로딩 종료
    }
  };

  // 책 선택 처리
  const handleSelectBook = (book) => {
    setSelectedBook({
      title: book.title.replace(/<b>|<\/b>/g, ""),
      author: book.author,
      description: book.description || "설명 없음",
      image: book.image,
      price: book.discount || book.price,
      stock_quantity: 10, // 기본값
    });
  };

  // 책 추가 처리
  const handleAddBook = async () => {
    if (!selectedBook) return alert("책을 선택해주세요!");

    try {
      await axios.post("/api/books", selectedBook);
      alert("책이 성공적으로 추가되었습니다!");
      setSelectedBook(null); // 책 선택 초기화
    } catch (error) {
      console.error("책 추가 실패:", error);
    }
  };

  return (
    <div className="section book-update">
      <div className="page-title">📚 서적 등록</div>
      <div className="search-box">
        <input
          type="text"
          placeholder="책 제목을 입력하세요..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>
      {loading && <div>로딩 중...</div>} {/* 로딩 표시 */}
      {bookList.length > 0 && (
        <ul className="search-results">
          {bookList.map((item, index) => (
            <li key={index} onClick={() => handleSelectBook(item)}>
              <img src={item.image} alt={item.title} width="50" />
              {item.title.replace(/<b>|<\/b>/g, "")} - {item.author}
            </li>
          ))}
        </ul>
      )}
      {selectedBook && (
        <div className="book-form">
          <label>제목:</label>
          <input type="text" value={selectedBook.title} readOnly />

          <label>저자:</label>
          <input type="text" value={selectedBook.author} readOnly />

          <label>설명:</label>
          <textarea value={selectedBook.description} readOnly />

          <label>가격:</label>
          <input type="number" value={selectedBook.price} readOnly />

          <label>재고 수량:</label>
          <input type="number" value={selectedBook.stock_quantity} readOnly />

          <label>표지 이미지:</label>
          {selectedBook.image && (
            <img src={selectedBook.image} alt="책 표지" width="100" />
          )}

          <button onClick={handleAddBook}>책 추가</button>
        </div>
      )}
    </div>
  );
};

export default BookUpdate;
