import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const BookView = () => {
  const { bookId } = useParams(); // URL에서 bookId를 가져옴
  const [book, setBook] = useState(null);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_BACK_SERVER}/api/books/${bookId}`)
      .then((res) => {
        setBook(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [bookId]);

  if (!book) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{book.title}</h1>
      <div className="book-details">
        <span className="author">저자: {book.author}</span>
        <span className="publisher">출판사: {book.publisher}</span>
      </div>
      <p>{book.description}</p>
    </div>
  );
};

export default BookView;
