import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import BookUpdate from "./BookUpdate";
import "./book.css";
import axios from "axios";
import PageNavi from "./PageNavi";

const BookList = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [bookList, setBookList] = useState([]);
  const [reqPage, setReqPage] = useState(1);
  const [pi, setPi] = useState({});
  useEffect(() => {
    axios
      .get(`${backServer}/api/books/${reqPage}`)
      .then((res) => {
        console.log(res);
        setBookList(res.data.list); //목록
        setPi(res.data.pi); //페이징
      })
      .catch((err) => {
        console.log(err);
      });
  }, [reqPage]);
  return (
    <section className="section" style={{ width: "100%" }}>
      <div className="page-title">Book List</div>
      <Link to="/book/update" className="btn-primary">
        책등록
      </Link>
      <div className="book-list-wrap">
        <ul className="list-wrap">
          {boardList.map((board, i) => {
            return <BookItem key={"board=" + i} board={board} />;
          })}
        </ul>
      </div>
      <div className="board-paging-warp">
        <PageNavi pi={pi} reqPage={reqPage} setReqPage={setReqPage} />
      </div>
    </section>
  );
};
const BookItem = (props) => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const board = props.board;
  const navigate = useNavigate();
  return (
    <li
      className="posting-item"
      onClick={() => {
        navigate(`/board/view/${board.boardNo}`);
      }}
    >
      <div className="posting-img">
        <img
          src={
            board.boardThumb
              ? `${backServer}/board/thumb/${board.boardThumb}`
              : "/image/default_img.png"
          }
        />
      </div>
      <div className="postion-info">
        <div className="posting-title">{board.boardTitle}</div>
        <div className="posting-sub-info">
          <span>{board.boardWriter}</span>
          <span>{board.boardDate}</span>
        </div>
      </div>
    </li>
  );
};
export default BookList;
