import React, { useState } from "react";
import axios from "axios";

const BookUpdate = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookList, setBookList] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [loading, setLoading] = useState(false);

  // 책 검색 처리
  const handleSearch = async () => {
    if (!searchTitle) return;

    setLoading(true);

    try {
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: { query: searchTitle },
      });

      console.log(response.data);

      let bookData = response.data;

      // 응답이 객체라면 배열로 변환
      if (!Array.isArray(bookData)) {
        bookData = [bookData];
      }

      const formattedBooks = bookData.map((item) => ({
        title: item.title || "제목 없음",
        author: item.author || "저자 정보 없음",
        description: item.description || "설명 없음",
        image: item.image || "",
        price: item.price || "가격 정보 없음",
      }));

      setBookList(formattedBooks);
    } catch (error) {
      console.error("책 검색 실패:", error);
    } finally {
      setLoading(false);
    }
  };

  // 책 선택 처리
  const handleSelectBook = (bookData) => {
    setSelectedBook({
      title: bookData.title.replace(/<b>|<\/b>/g, ""), // HTML 태그 제거
      author: bookData.author,
      description: bookData.description,
      image: bookData.image,
      price: bookData.price,
      stock_quantity: 10, // 기본 재고 수량 설정
    });
  };

  // 책 추가 처리
  const handleAddBook = async () => {
    if (!selectedBook) return alert("책을 선택해주세요!");

    try {
      await axios.post(`${backServer}/api/books`, selectedBook);
      alert("책이 성공적으로 추가되었습니다!");
      setSelectedBook(null);
    } catch (error) {
      console.error("책 추가 실패:", error);
    }
  };

  return (
    <div className="section book-update">
      <div className="page-title">📚 서적 등록</div>
      <div className="search-box">
        <input
          type="text"
          placeholder="책 제목을 입력하세요..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>검색</button>
      </div>
      {loading && <div>로딩 중...</div>} {/* 로딩 표시 */}
      {bookList.length > 0 && (
        <ul className="search-results">
          {bookList.map((book, index) => (
            <li key={index} onClick={() => handleSelectBook(book)}>
              <img src={book.image} alt={book.title} width="50" />
              {book.title} - {book.author}
            </li>
          ))}
        </ul>
      )}
      {selectedBook && (
        <div className="book-form">
          <label>제목:</label>
          <input type="text" value={selectedBook.title} readOnly />

          <label>저자:</label>
          <input type="text" value={selectedBook.author} readOnly />

          <label>설명:</label>
          <textarea value={selectedBook.description} readOnly />

          <label>가격:</label>
          <input type="text" value={selectedBook.price} readOnly />

          <label>재고 수량:</label>
          <input type="number" value={selectedBook.stock_quantity} readOnly />

          <label>표지 이미지:</label>
          {selectedBook.image && (
            <img src={selectedBook.image} alt="책 표지" width="100" />
          )}

          <button onClick={handleAddBook}>책 추가</button>
        </div>
      )}
    </div>
  );
};

export default BookUpdate;
