import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const BookItem = (props) => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const book = props.book;
  const navigate = useNavigate();

  // book이 유효한지 확인하고 초기값 설정
  const [stockCount, setStockCount] = useState(book ? book.stockCount : 0); // book이 존재하면 stockCount 사용, 없으면 0으로 설정

  useEffect(() => {
    if (book) {
      setStockCount(book.stockCount); // book이 로드되면 stockCount를 다시 설정
    }
  }, [book]);

  // 수량 변경 처리 함수
  const handleStockChange = (type) => {
    let newStockCount = stockCount;
    if (type === "increase") {
      newStockCount += 1;
    } else if (type === "decrease" && stockCount > 0) {
      newStockCount -= 1;
    }

    setStockCount(newStockCount);

    // 서버로 업데이트된 재고 수량을 전송
    axios
      .put(`${backServer}/api/books/${book.id}/update`, {
        stockCount: newStockCount,
      })
      .then((res) => {
        console.log("재고가 성공적으로 업데이트되었습니다.", res);
      })
      .catch((err) => {
        console.log("재고 업데이트 중 오류 발생", err);
      });
  };

  // book이 없을 경우 로딩 상태 표시
  if (!book) {
    return <div>Loading...</div>; // book이 로드되지 않았을 때 표시할 메시지
  }

  return (
    <li
      className="posting-item"
      onClick={() => {
        navigate(`/board/view/${book.id}`);
      }}
    >
      <div className="posting-img">
        <img
          src={book.image ? book.image : "/image/default_img.png"}
          alt={book.title}
        />
      </div>
      <div className="posting-info">
        <div className="posting-title">{book.title}</div>
        <div className="posting-sub-info">
          <span>저자 : {book.author}</span>
          <span>출판사 : {book.publisher}</span>
          <span>판매 : {book.salesCount}</span>
          <span>수량 : {stockCount}</span>
        </div>
        <div className="stock-controls">
          <button onClick={() => handleStockChange("decrease")}>-</button>
          <button onClick={() => handleStockChange("increase")}>+</button>
        </div>
      </div>
    </li>
  );
};

export default BookItem;
