import React, { useState } from "react";
import axios from "axios";

const BookUpdate = () => {
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookList, setBookList] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [loading, setLoading] = useState(false);

  // Ï±Ö Í≤ÄÏÉâ Ï≤òÎ¶¨
  const handleSearch = async () => {
    if (!searchTitle) return;

    setLoading(true);

    try {
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: { query: searchTitle },
      });

      console.log(response.data);

      // ÏùëÎãµ Îç∞Ïù¥ÌÑ∞Î•º map Íµ¨Ï°∞Ïóê ÎßûÍ≤å Î≥ÄÌôò
      const formattedBooks = response.data.map((bookData) => ({
        title: bookData["title"] || "Ï†úÎ™© ÏóÜÏùå",
        author: bookData["author"] || "Ï†ÄÏûê Ï†ïÎ≥¥ ÏóÜÏùå",
        description: bookData["description"] || "ÏÑ§Î™Ö ÏóÜÏùå",
        image: bookData["image"] || "",
        price: bookData["price"] || "Í∞ÄÍ≤© Ï†ïÎ≥¥ ÏóÜÏùå",
      }));

      setBookList(formattedBooks);
    } catch (error) {
      console.error("Ï±Ö Í≤ÄÏÉâ Ïã§Ìå®:", error);
    } finally {
      setLoading(false);
    }
  };

  // Ï±Ö ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleSelectBook = (bookData) => {
    setSelectedBook({
      title: bookData.title.replace(/<b>|<\/b>/g, ""), // HTML ÌÉúÍ∑∏ Ï†úÍ±∞
      author: bookData.author,
      description: bookData.description,
      image: bookData.image,
      price: bookData.price,
      stock_quantity: 10, // Í∏∞Î≥∏ Ïû¨Í≥† ÏàòÎüâ ÏÑ§Ï†ï
    });
  };

  // Ï±Ö Ï∂îÍ∞Ä Ï≤òÎ¶¨
  const handleAddBook = async () => {
    if (!selectedBook) return alert("Ï±ÖÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî!");

    try {
      await axios.post(`${backServer}/api/books`, selectedBook);
      alert("Ï±ÖÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!");
      setSelectedBook(null);
    } catch (error) {
      console.error("Ï±Ö Ï∂îÍ∞Ä Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="section book-update">
      <div className="page-title">üìö ÏÑúÏ†Å Îì±Î°ù</div>
      <div className="search-box">
        <input
          type="text"
          placeholder="Ï±Ö Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>Í≤ÄÏÉâ</button>
      </div>
      {loading && <div>Î°úÎî© Ï§ë...</div>} {/* Î°úÎî© ÌëúÏãú */}
      {bookList.length > 0 && (
        <ul className="search-results">
          {bookList.map((book, index) => (
            <li key={index} onClick={() => handleSelectBook(book)}>
              <img src={book.image} alt={book.title} width="50" />
              {book.title} - {book.author}
            </li>
          ))}
        </ul>
      )}
      {selectedBook && (
        <div className="book-form">
          <label>Ï†úÎ™©:</label>
          <input type="text" value={selectedBook.title} readOnly />

          <label>Ï†ÄÏûê:</label>
          <input type="text" value={selectedBook.author} readOnly />

          <label>ÏÑ§Î™Ö:</label>
          <textarea value={selectedBook.description} readOnly />

          <label>Í∞ÄÍ≤©:</label>
          <input type="text" value={selectedBook.price} readOnly />

          <label>Ïû¨Í≥† ÏàòÎüâ:</label>
          <input type="number" value={selectedBook.stock_quantity} readOnly />

          <label>ÌëúÏßÄ Ïù¥ÎØ∏ÏßÄ:</label>
          {selectedBook.image && (
            <img src={selectedBook.image} alt="Ï±Ö ÌëúÏßÄ" width="100" />
          )}

          <button onClick={handleAddBook}>Ï±Ö Ï∂îÍ∞Ä</button>
        </div>
      )}
    </div>
  );
};

export default BookUpdate;
