import React, { useState } from "react";
import axios from "axios";

const BookUpdate = () => {
  const NAVER_CLIENT_ID = process.env.REACT_APP_NAVER_CLIENT_ID;
  const NAVER_CLIENT_SECRET = process.env.REACT_APP_NAVER_CLIENT_SECRET;
  const backServer = process.env.REACT_APP_BACK_SERVER;
  const [searchTitle, setSearchTitle] = useState("");
  const [bookList, setBookList] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [loading, setLoading] = useState(false);

  // Ï±Ö Í≤ÄÏÉâ Ï≤òÎ¶¨
  const handleSearch = async () => {
    if (!searchTitle) return;

    setLoading(true); // Î°úÎî© ÏãúÏûë

    try {
      // Î∞±ÏóîÎìú ÏÑúÎ≤ÑÎ•º ÌÜµÌï¥ ÎÑ§Ïù¥Î≤Ñ APIÏóê ÏöîÏ≤≠
      const response = await axios.get(`${backServer}/api/search-books`, {
        params: {
          query: searchTitle,
        },
      });
      console.log(response);
      // ÎÑ§Ïù¥Î≤Ñ APIÏóêÏÑú XML ÏùëÎãµÏùÑ Î∞õÏúºÎ©¥ Ï†ÅÏ†àÌûà ÌååÏã±ÌïòÏó¨ bookListÏóê Ï†ÄÏû•
      const bookData = response.data;
      setBookList(bookData); // Ïã§Ï†ú API ÏùëÎãµÏóê ÎßûÍ≤å ÌååÏã± ÌïÑÏöî
    } catch (error) {
      console.error("Ï±Ö Í≤ÄÏÉâ Ïã§Ìå®:", error);
    } finally {
      setLoading(false); // Î°úÎî© Ï¢ÖÎ£å
    }
  };

  // Ï±Ö ÏÑ†ÌÉù Ï≤òÎ¶¨
  const handleSelectBook = (book) => {
    setSelectedBook({
      title: book.title.replace(/<b>|<\/b>/g, ""),
      author: book.author,
      description: book.description || "ÏÑ§Î™Ö ÏóÜÏùå",
      image: book.image,
      price: book.discount || book.price,
      stock_quantity: 10, // Í∏∞Î≥∏Í∞í
    });
  };

  // Ï±Ö Ï∂îÍ∞Ä Ï≤òÎ¶¨
  const handleAddBook = async () => {
    if (!selectedBook) return alert("Ï±ÖÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî!");

    try {
      await axios.post("/api/books", selectedBook);
      alert("Ï±ÖÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!");
      setSelectedBook(null); // Ï±Ö ÏÑ†ÌÉù Ï¥àÍ∏∞Ìôî
    } catch (error) {
      console.error("Ï±Ö Ï∂îÍ∞Ä Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="section book-update">
      <div className="page-title">üìö ÏÑúÏ†Å Îì±Î°ù</div>
      <div className="search-box">
        <input
          type="text"
          placeholder="Ï±Ö Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
        />
        <button onClick={handleSearch}>Í≤ÄÏÉâ</button>
      </div>
      {loading && <div>Î°úÎî© Ï§ë...</div>} {/* Î°úÎî© ÌëúÏãú */}
      {bookList.length > 0 && (
        <ul className="search-results">
          {bookList.map((item, index) => (
            <li key={index} onClick={() => handleSelectBook(item)}>
              <img src={item.image} alt={item.title} width="50" />
              {item.title.replace(/<b>|<\/b>/g, "")} - {item.author}
            </li>
          ))}
        </ul>
      )}
      {selectedBook && (
        <div className="book-form">
          <label>Ï†úÎ™©:</label>
          <input type="text" value={selectedBook.title} readOnly />

          <label>Ï†ÄÏûê:</label>
          <input type="text" value={selectedBook.author} readOnly />

          <label>ÏÑ§Î™Ö:</label>
          <textarea value={selectedBook.description} readOnly />

          <label>Í∞ÄÍ≤©:</label>
          <input type="number" value={selectedBook.price} readOnly />

          <label>Ïû¨Í≥† ÏàòÎüâ:</label>
          <input type="number" value={selectedBook.stock_quantity} readOnly />

          <label>ÌëúÏßÄ Ïù¥ÎØ∏ÏßÄ:</label>
          {selectedBook.image && (
            <img src={selectedBook.image} alt="Ï±Ö ÌëúÏßÄ" width="100" />
          )}

          <button onClick={handleAddBook}>Ï±Ö Ï∂îÍ∞Ä</button>
        </div>
      )}
    </div>
  );
};

export default BookUpdate;
